import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const button = document.querySelector(".add");
    const nom = document.querySelector("#nom");
    const title = document.querySelector("h1");
    const dflex = document.querySelector(".d-flex");
    const number = document.querySelector("#age");
    const clear = document.querySelector("#clear");

    console.log(localStorage.getItem("nom"));
    if (localStorage.getItem("nom"))
      title.textContent = `Hello ${localStorage.getItem(
        "nom"
      )} j'ai  ${localStorage.getItem("age")}`;
    else {
    }
    button.onclick = () => {
      const user = {
        nom: nom.value,
        age: number.value,
      };
      localStorage.setItem("nom", JSON.stringify(user));

      document.location.reload();
    };
    clear.onclick = () => {
      localStorage.clear();
      document.location.reload();
    };
  });

  return (
    <div className="d-flex flex-column mx-auto text-center justify-content-center align-content-center ">
      <h1>Bonjour inconnu</h1>
      <div className="d-flex flex-column text-center w-50">
        <label htmlFor="nom">Your name?</label>
        <input
          type="text"
          name="nom"
          id="nom"
          placeholder="nom?"
          className=""
        />
        <div className="d-flex flex-column text-center">
          {/* Age */}
          <label htmlFor="age">Your age?</label>
          <input
            type="number"
            name="age"
            id="age"
            placeholder="age?"
            className=""
          />
          <div className="d-flex flex-column text-center">
            <button id="button" className="add btn btn-warning text-light">
              Add
            </button>
          </div>
        </div>
        <button id="clear" className="btn btn-primary">
          CLEAR
        </button>
      </div>
    </div>
  );
};

export default App;
