import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const button = document.querySelector("#button");
    const nom = document.querySelector("#nom");
    const title = document.querySelector("h1");
    const dflex = document.querySelector(".d-flex");

    console.log(localStorage.getItem("nom"));
    if (localStorage.getItem("nom"))
      title.textContent = `Hello ${localStorage.getItem("nom")}`;
    else {
    }
    button.onclick = () => {
      localStorage.setItem("nom", nom.value);
    };
  });

  return (
    <div className="d-flex flex-column mx-auto text-center justify-content-center align-content-center ">
      <h1>Bonjour inconnu</h1>
      <div className="d-flex flex-column text-center w-50">
        <label htmlFor="nom">Your name?</label>
        <input
          type="text"
          name="nom"
          id="nom"
          placeholder="nom?"
          className=""
        />
        <div className="d-flex flex-column text-center">
          <button id="button" className="btn btn-primary">
            Add
          </button>
          {/* Age */}
          <label htmlFor="age">Your age?</label>
          <input
            type="number"
            name="age"
            id="age"
            placeholder="age?"
            className=""
          />
          <div className="d-flex flex-column text-center">
            <button id="button" className="btn btn-warning text-light">
              Add
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
