import React, { useEffect, useState } from "react";

const App = () => {
  const [listeAdd, setListAdd] = useState("ok");
  useEffect(() => {
    const ul = document.querySelector("ul");
    const uneListe = document.querySelector("li");
    const input = document.querySelector('input[type="text"]');
    const form = document.querySelector("form");
    const btn = document.querySelector(".btn");

    const valider = () => {
      input.addEventListener("input", (e) => {
        let createListe = document.createElement("li");
        ul.appendChild(createListe);
        createListe.textContent = e.target.value;
        console.log(e.target.value);
      });
    };
    btn.addEventListener("click", valider());
  }, []);

  return (
    <div className="container ">
      <div className="block">
        <div className="text-center rec">
          <h1 className="mt-5 pt-3">My To Do List</h1>

          <form action="">
            <input type="text" name="" id="" className="w-50 mt-3" />
            <button type="submit" className="btn btn-primary">
              Add
            </button>
          </form>
        </div>

        <ul className="list-group">
          <li className="list-group-item list-group-item-success">
            A simple success list group item
          </li>
        </ul>
      </div>
    </div>
  );
};

export default App;
