import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const name = document.querySelector("#name");
    const age = document.querySelector("#age");
    const title = document.querySelector("h1");
    const voiture = {
      name: name.age,
      age: age.value,
    };
    if (localStorage.getItem("json")) {
      title.textContent = `Hello ${voiture.name}`;
    }

    localStorage.setItem("json", JSON.stringify(voiture));
  }, []);

  localStorage.setItem("name", "Moussa");
  return (
    <div className="container text-center d-flex justify-content-center mt-5">
      <div className="bloc d-flex justify-content-center flex-column ">
        <h1>Bienvenue Ã  votre interface utilisateur </h1>
        <div className="blocForm d-flex flex-column w-auto">
          <input type="text" name="name" id="name" placeholder="your name" />
          <input type="number" name="age" id="age" placeholder="your age" />
          <div className="buttonBlocks">
            <button className="btn btn-primary w-50">Add</button>
            <button
              className="btn btn-danger w-50"
              onClick={() => {
                localStorage.clear();
                document.location.reload();
              }}
            >
              Clear
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
