import React, { useEffect } from "react";

const App = () => {
  const inputName = document.querySelector("#name");
  const inputAge = document.querySelector("#age");
  const title = document.querySelector("h1");
  const add = document.querySelector(".add");
  // title.textContent = `Hello ${voiture.name}`;
  const myJson = JSON.parse(localStorage.getItem("myCar"));

  const addData = () => {
    const car = {
      name: inputName.value,
      age: inputAge.value,
    };
    localStorage.setItem("myCar", JSON.stringify(car));
    document.location.reload();
  };

  return (
    <div className="container text-center d-flex justify-content-center mt-5">
      <div className="bloc d-flex justify-content-center flex-column ">
        <h1>Bonjour cher utilasateur </h1>
        <div className="blocForm d-flex flex-column w-auto">
          <input type="text" name="name" id="name" placeholder="your name" />
          <input type="number" name="age" id="age" placeholder="your age" />
          <div className="buttonBlocks">
            <button
              className="btn btn-danger w-50"
              onClick={() => {
                localStorage.clear();
                document.location.reload();
              }}
            >
              Clear
            </button>
            <button
              className="add btn btn-primary w-50"
              onClick={() => addData()}
            >
              {" "}
              Add
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
